import React, { useState } from "react";
import styled from "styled-components";

const Todo = () => {
  const [todoInserModal, setTodoInsertModal] = useState(false);

  const activeInsertModal = () => {
    setTodoInsertModal(true);
  };

  return (
    <TodoWrapper>
      <TodoUserInfo>
        <span>님 환영합니다!</span>
        <LogOutButton>로그아웃</LogOutButton>
      </TodoUserInfo>
      <TodoListBody>
        <TodoListTitle>
          ToDo List
          <TodoButtonWrapper>
            <InsertButton>추가</InsertButton>
          </TodoButtonWrapper>
        </TodoListTitle>
        {todoInserModal ? <TodoInsertModal /> : ""}
      </TodoListBody>
    </TodoWrapper>
  );
};

export default Todo;

const TodoWrapper = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

const TodoUserInfo = styled.div`
  margin: 1rem 0;
  width: 100%;
  height: 20%;
  display: flex;
  justify-content: space-around;
  align-items: center;
`;

const LogOutButton = styled.button`
  width: 100px;
  height: 40px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  background-color: #36f;
  color: white;

  &:hover {
    background-color: #002b5b;
  }
`;

const TodoListBody = styled.div`
  height: 80%;
`;

const TodoListTitle = styled.div`
  position: relative;
  width: 810px;
  height: 4rem;
  background: #22b8cf;
  font-size: 1.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  margin-left: auto;
  margin-right: auto;
  margin-top: 6rem;
  border-radius: 4px;
  overflow: hidden;
`;

const TodoButtonWrapper = styled.div`
  width: 810px;
  height: 2rem;
  position: absolute;
  left: 710px;
  top: 0px;
`;

const InsertButton = styled.button`
  width: 100px;
  height: 4rem;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  background-color: #36f;
  color: white;

  &:hover {
    background-color: #002b5b;
  }
`;
