import React, { useCallback, useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const navigate = useNavigate();

  const onSignUp = () => {
    navigate("/auth/signup");
  };

  const onSubmit = useCallback(
    async e => {
      e.preventDefault();
      try {
        await axios
          .post("http://localhost:8080/users/login", {
            email: email,
            password: password,
          })
          .then(res => {
            if (res.ok) {
              navigate("/");
            }
          });
      } catch (err) {
        console.error(err);
      }
    },
    [email, password, navigate]
  );

  return (
    <LoginBody>
      <LoginWrapper>
        <LoginIntro>
          프리온보딩 <br />
          프론트엔드 챌린지 8월
        </LoginIntro>
        <EmailWrapper>
          <p>이메일</p>
          <LoginInput type="email" placeholder="이메일을 입력해 주세요." />
        </EmailWrapper>
        <PasswordWrapper>
          <p>비밀번호</p>
          <LoginInput type="password" placeholder="비밀번호를 입력해 주세요." />
        </PasswordWrapper>
        <LoginButton type="submit">로그인</LoginButton>
        <SignUpButton type="button" onClick={onSignUp}>
          회원가입
        </SignUpButton>
      </LoginWrapper>
    </LoginBody>
  );
};

export default Login;

const LoginBody = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  border: 1px solid black;
  background-color: rgba(0, 0, 0, 0.3);
`;

const LoginWrapper = styled.div`
  width: 25rem;
  height: 38.375rem;
  border: none;
  border-radius: 0.313rem;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  background-color: white;
`;

const LoginIntro = styled.p`
  text-align: center;
  font-size: 20px;
  font-weight: bold;
  margin: 3rem 0;
`;

const EmailWrapper = styled.div`
  width: 80%;
  margin: 0.5rem 0;

  p {
    margin: 0;
    font-size: 15px;
    color: #767676;
  }
`;

const PasswordWrapper = styled.div`
  width: 80%;
  margin: 0.5rem 0;

  p {
    margin: 0;
    font-size: 15px;
    color: #767676;
  }
`;

const LoginInput = styled.input`
  height: 3.125rem;
  width: 90%;
  margin: 0.5rem 0;
  border: 1px solid #e1e2e3;
  border-radius: 0.313rem;
  padding: 0 0.938rem;
`;

const LoginButton = styled.button`
  height: 3.125rem;
  width: 80%;
  margin: 3rem 0 0.5rem 0;
  background-color: #36f;
  color: #fff;
  border: none;
  cursor: pointer;
  border-radius: 1.688rem;
  font-size: 15px;
  display: flex;
  justify-content: center;
  align-items: center;

  &:hover {
    background-color: #002b5b;
  }
`;

const SignUpButton = styled.button`
  height: 3.125rem;
  width: 80%;
  margin: 0.5rem 0;
  background-color: #36f;
  color: #fff;
  border: none;
  cursor: pointer;
  border-radius: 1.688rem;
  font-size: 15px;
  display: flex;
  justify-content: center;
  align-items: center;

  &:hover {
    background-color: #002b5b;
  }
`;
