import React, { useState } from "react";
import styled from "styled-components";

const SignUp = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  return (
    <SignUpBody>
      <SignUpWrapper>
        <p>회원가입</p>
        <EmailWrapper>
          <p>이메일</p>
          <EmailInput placeholder="이메일을 입력해 주세요." />
        </EmailWrapper>
        <PassWordWrapper>
          <p>비밀번호</p>
          <PasswordInput placeholder="비밀번호를 입력해 주세요." />
        </PassWordWrapper>
        <PassWordCheckWrapper>
          <p>비밀번호 확인</p>
          <PasswordCheckInput placeholder="비밀번호를 다시 입력해 주세요." />
        </PassWordCheckWrapper>
        <SubmitWrapper>
          <SubmitButton>회원가입</SubmitButton>
        </SubmitWrapper>
      </SignUpWrapper>
    </SignUpBody>
  );
};

export default SignUp;

const SignUpBody = styled.div`
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const SignUpWrapper = styled.div`
  width: 45%;
  height: 100vh;
  border-radius: 0.313rem;
  border: 1px solid #e1e2e3;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;

  p {
    font-weight: bold;
  }
`;

const EmailWrapper = styled.div`
  width: 80%;
  margin: 1rem 0;

  p {
    margin-bottom: 0.5rem;
    font-size: 15px;
    color: #767676;
  }
`;

const PassWordWrapper = styled.div`
  width: 80%;
  margin: 1rem 0;

  p {
    margin-bottom: 0.5rem;
    font-size: 15px;
    color: #767676;
  }
`;

const PassWordCheckWrapper = styled.div`
  width: 80%;
  margin: 1rem 0;

  p {
    margin-bottom: 0.5rem;
    font-size: 15px;
    color: #767676;
  }
`;

const EmailInput = styled.input`
  height: 3.125rem;
  width: 100%;
  border-radius: 0.313rem;
  border: 1px solid #e1e2e3;
  padding: 0 0.938rem;
`;

const PasswordInput = styled.input`
  height: 3.125rem;
  width: 100%;
  border: 1px solid #e1e2e3;
  border-radius: 0.313rem;
  padding: 0 0.938rem;
`;

const PasswordCheckInput = styled.input`
  height: 3.125rem;
  width: 100%;
  border: 1px solid #e1e2e3;
  border-radius: 0.313rem;
  padding: 0 0.938rem;
`;

const SubmitWrapper = styled.div`
  width: 80%;
  margin: 5rem 0;
`;

const SubmitButton = styled.button`
  width: 100%;
  height: 3.125rem;
  border: none;
  border-radius: 1.688rem;
  background-color: #36f;
  color: white;
  cursor: pointer;

  &:hover {
    background-color: #002b5b;
  }
`;
